mapper.insert:
insert into look
values
(
#{look_num},#{title},#{like},#{season},#{look_privacy},#{fk_userid_user_userid}
,#{memo},#{look_date},#{count},#{comment_count},#{nickname}
)



    SELECT LAST_INSERT_ID()

mapper.select:
select *
from look
WHERE (fk_userid_user_userid=#{userid1} or fk_userid_user_userid=#{userid2} or fk_userid_user_userid=#{userid3} or fk_userid_user_userid=#{userid4} or fk_userid_user_userid=#{userid5}) AND look_num < #{looknum} order by look_num desc LIMIT 6;

mapper.update:
 update look set title = #{title},season = #{season},
 look_privacy = #{look_privacy},memo = #{memo}
where look_num = #{look_num}
